(ns advent.sixteen.a2-test
  (:require [advent.sixteen.a2 :as sut]
            [clojure.test :as t]))

(t/deftest single-movements
  (t/are [x y] (= x y)
    4 (sut/follow 4 \L)
    5 (sut/follow 4 \R)
    6 (sut/follow 6 \R)
    5 (sut/follow 6 \L)))


(t/deftest example
  (t/is (= [1 9 8 5]
         (sut/find-toilet-code '(ULL
                                 RRDDD
                                 LURDL
                                 UUUUD)))))

(def input
  '(



))

(sut/find-toilet-code input)
